package hello_java;

public class Java0808 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
       int age = 50; // 숫자를 대입함.
        // <- 주석 :설명을 적을 수 있다! // int는 데이터타입이고 age는 변수이름(바꿀 수 있)이다.
        // 데이터 타입은 변경 불가능!!!!!!!! 변수만 자유롭게! 개 이름 짓듯이 바꿀 수 있다
        // 프로그래밍에서 '=' 의 뜻은? 같다가 아니라!!! 대입을 의미함!!
        // 데이터타입이 int이기 때문에 숫자만 대입할 수 있다. (맨시티? 안됨!)
       
        // 문제 1. 데이터 타입은 int, 변수 이름은 money라고 할때,  500을 대입하시오
       int money = 500; // <- 마지막에 ;(세미콜론)을 찍어야 컴퓨터가 코드를 실행할 수 있다.
       System.out.println(money); //syso 누르고 컨트롤+스페이스! // 괄호에 변수이름을 넣고 
       // 저장(컨+s) 후 실행!**(컨+f11)
       String 이름 = "김민서"; // 문자는 꼭 큰 따옴표로 써야한다!!
       System.out.println("당신 이름은? "+이름);
       String 사는곳 = "대전";
    		  System.out.println("제가 사는 곳은? " +사는곳);
       // 문제 2. 데이터타입 String 변수이름 취미 // 본인의 취미 데이터를 대입 후 출력!
       String 취미 = "주변탐방";
     System.out.println("당신의 취미는 뭔가요? " + 취미);// 프로그래밍은 위에서 아래로 해석!!
     int x = 10; //x라는 변수를 선언하다. 
     System.out.println("x의 값은? "+x);
     //(int가 생략 된 거나 마찬가지.) 
     x = 20; // 기존에 있던 10은 사라지고 20이 된다.
    System.out.println("x의 값은? " +x);
     x = 50;
     System.out.println("x의 값은? " + x);
     // = 대입은 기존데이터를 무시하고 새 데이터 넣기? 쌉가능~                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       
     // = 는 대입. = 곧 연산자이더. 풀네임은 대입  the 연산자.
     // 연산자의 종류: +, -, /(나누기 몫값), %(나누기 나머지값), = (대입) 
     
     int a = 5; //5를 데이터 타입이 int의 변수에 대입함.
     int b = 10; // 10을 데이더 타입 int인 b에 대입함.
     int c = a+b; // a와 b를 다한 값을 데이터 타입 int인 c에 대입함.
     System.out.println("최종 값은? "+c);
     
     // 문제 4. 10-7을 구현하는 로직을 아래에 작성하시오.
     int d = 10;
     int e = 7;
     int f = d-e;
     System.out.println("최종 값은? "+f);
	}

}
